exports.dateStringToDate = function (str) {
  var d = new Date()
  d.setFullYear(parseInt(str.slice(0, 4), 10))    // Year
  d.setMonth(parseInt(str.slice(4, 6), 10) - 1) // Month
  d.setDate(parseInt(str.slice(6, 8), 10))     // Day
  d.setHours(parseInt(str.slice(9, 11), 10))    // Hour
  d.setMinutes(parseInt(str.slice(12, 14), 10))   // Minute
  d.setSeconds(parseInt(str.slice(15, 17), 10))   // Second

  return d
}
